import { createStore, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import reducer from './reducers/root';

export default function configureStore(initialState={}) {
    return createStore(
        reducer,
        initialState,
        applyMiddleware(thunk)
    );
}
