import { TEMPERATURE_SETTING } from '../actions/traits';

export const trait = (state = {}, action) => {
    switch (action.type) {
        case TEMPERATURE_SETTING: {
            return {
                ...state,
                temperatureSetting: {
                    [action.data.id]: {
                        thermostatTemperatureAmbient: action.data.temperature,
                        thermostatHumidityAmbient: action.data.humidity
                    }
                }
            }
        }
        default:
            return state;
    }
}
