import { ON_OFF, TEMPERATURE_SETTING } from '../actions';

const devices = (state = {}, action) => {
    switch (action.type) {
        case ON_OFF: {
            return {
                ...state,
                [action.id]: {
                    ...state[action.id],
                    onOff: {
                        on: action.on
                    }
                }
            }
        }
        case TEMPERATURE_SETTING: {
            console.log("Temperat")
            return {
                ...state,
                [action.data.id]: {
                    ...state[action.data.id],
                    temperatureSetting: {
                        thermostatTemperatureAmbient: action.data.temperature,
                        thermostatHumidityAmbient: action.data.humidity
                    }
                }
            }
        }
        default:
      return state;
    }
}

export default devices
